let webdriver = require('selenium-webdriver'),
    By = webdriver.By,
    until = webdriver.until;

var Multiton = (function () {    
    var arrayInstances = [],
    maxInstancesInArray = 4;
         
     var Multiton = function () {
         this.arrayInstances = arrayInstances;   
     };
 
     return { 
         setInstance: function() {
            if (arrayInstances.length < maxInstancesInArray){
                var driver = new webdriver.Builder()
                .forBrowser('chrome')
                .build();
                arrayInstances.push(driver);
             }         
             return driver;
         }
     };
 })();              

var firstDriver = Multiton.setInstance();
var secondDriver = Multiton.setInstance();

firstDriver.get('http://www.bbc.com/');
secondDriver.get('http://www.bbc.com/');