let webdriver = require('selenium-webdriver'),
    By = webdriver.By,
    until = webdriver.until;

(function RunFirefoxBrowser() {
    let driverFirefox = new webdriver.Builder()
        .forBrowser('firefox')
        .build();
    driverFirefox.get('http://www.bbc.com/');
    driverFirefox.findElement(By.id("orb-search-q")).sendKeys("hello");
    driverFirefox.manage().window().maximize();
    driverFirefox.findElement(By.css("#orb-nav-links > ul > li:nth-child(1)")).click(); 
})();


(function RunChromeBrowser() {
    let driverChrome = new webdriver.Builder()
        .forBrowser('chrome')
        .build();
        driverChrome.get('http://www.bbc.com/');
        driverChrome.findElement(By.id("orb-search-q")).sendKeys("hello");
        driverChrome.manage().window().maximize();
        driverChrome.findElement(By.css("#orb-nav-links > ul > li:nth-child(1)")).click();        
})();

(async function RunIEBrowser() {
    let driverIE = await new webdriver.Builder()
        .forBrowser('internet explorer')
        .build();
    await driverIE.manage().window().maximize();
    await driverIE.get('http://www.bbc.com/');
    await driverIE.findElement(By.css(".orb-nav-search")).click();    
    await driverIE.findElement(By.id('se-searchbox-input-field')).sendKeys("Hello");
    await driverIE.findElement(By.css("#orb-nav-links > ul > li:nth-child(1)")).click(); 
          
})();









