let webdriver = require('selenium-webdriver'),
    By = webdriver.By,
    until = webdriver.until;


var Singleton = (function () {
    var instance;

    function createInstance() {
       var driver = new webdriver.Builder()
            .forBrowser('firefox')
            .build();
        return driver;
    }

    return {
        getInstance: function () {
            if (!instance) {
                instance = createInstance();
            }
            return instance;
        }
    };
})();

var firstDriver = Singleton.getInstance();
var secondDriver = Singleton.getInstance();

firstDriver.get('http://www.bbc.com/');
secondDriver.get('http://www.bbc.com/');

